create table TsMarquee
(
	FId uuid primary key,
	FName varchar(50),
	FUserId uuid,
	FDepartmentId uuid,
	FStatus varchar(10),
	FPublishType varchar(10),
	FStartTime timestamp,
	FEndTime timestamp,
	FContent varchar(10000),
	FCreateUserId uuid,
	FCreateDepartmentId uuid,
	FCreateTime timestamp,
	FUpdateUserId uuid,
	FUpdateDepartmentId uuid,
	FUpdateTime timestamp
);

create table TsMarqueePublishRange
(
	FId uuid primary key,
	FMarqueeId uuid,
	FType varchar(50),
	FEntityId uuid,
	FName varchar(50)
);




